    st.attribute_id,
    st.permission_id
FROM
    (
    SELECT
    '1' AS permission_id,
    sa.attribute_id
    FROM
    `sys_attribute` sa
    WHERE
    sa.url = '/security/element/tree'
    OR sa.url = '/oauth2/sign-out'
    OR sa.url LIKE'%enum%'
    OR sa.url LIKE'%message%' UNION
    SELECT
    '2' AS permission_id,
    sa.attribute_id
    FROM
    `sys_attribute` sa
    WHERE
    sa.url LIKE'%authorize%'
    AND sa.url != '/authorize/constant/enums' UNION
    SELECT
    '3' AS permission_id,
    sa.attribute_id
    FROM
    `sys_attribute` sa
    WHERE
    sa.url LIKE'%security%'
    AND sa.url != '/security/element/tree' UNION
    SELECT
    '4' AS permission_id,
    sa.attribute_id
    FROM
    `sys_attribute` sa
    WHERE
    sa.url LIKE'%hr%' UNION
    SELECT
    '5' AS permission_id,
    sa.attribute_id
    FROM
    `sys_attribute` sa
    WHERE
    sa.url LIKE'%camunda-extended%' UNION
    SELECT
    '6' AS permission_id,
    sa.attribute_id
    FROM
    `sys_attribute` sa
    WHERE
    sa.url LIKE'%db%'
    OR sa.url LIKE'%asset%' UNION
    SELECT
    '7' AS permission_id,
    sa.attribute_id
    FROM
    `sys_attribute` sa
    WHERE
    sa.url LIKE'%meteorology%'
    OR sa.url LIKE'%satellite%'
    ) AS st;